
public class Swapping {
	public static void main(String[] args) {
		// TODO Auto-generated method stub		
		//Swapping of two numbers		
		int no1=10;
		int no2=20;
		int temp;
		temp=no1;
		no1=no2;
		no2=temp;
		System.out.println("swapped numbers=");
		System.out.println("no1="+no1);
		System.out.println("no2="+no2);
	}
}
